package preppers.weeks2.kiosk.consoleimport preppers.weeks2.kiosk.menu.Menuimport preppers.weeks2.kiosk.product.Productclass Console {    fun getInput(): String {        return readln()    }    fun showMenu() {        println(MENU.trimIndent())    }    fun showMenuBySelect(select: String): Menu {        return when (select) {            "1" -> return Menu.PLATE            "2" -> return Menu.PASTA            "3" -> return Menu.ROSE_PASTA            "4" -> return Menu.BOWL            "5" -> return Menu.DOUBLE_BOWL            "6" -> return Menu.DESSERT            "7" -> return Menu.DRINK            "8" -> return Menu.NONE            else -> throw IllegalArgumentException("해당 메뉴는 없습니다.")        }    }    fun showSaveOrders(list: List<Product>) {        list.forEachIndexed { idx, menu ->            println("${idx + 1}. ${menu.name} \t\t| ${menu.cost}원 \t\t| ${menu.description} ")        }        println("\n$ORDER_MENU")    }    fun showMenuDeepContent(category: Menu, list: List<Product>) {        println("[${category.name} MENU]")        list.forEachIndexed { idx, menu ->            println("${idx + 1}. ${menu.name} \t\t| ${menu.cost}원 \t\t| ${menu.description} ")        }        println(MOVE_BACK)    }    fun selectOrder(input: String, list: List<Product>): Product? {        when (input.toInt()) {            in 1 until list.size + 1 -> return list[input.toInt() - 1]            else -> throw IllegalArgumentException("해당 메뉴는 없습니다.")        }    }    companion object {        private const val MENU = """            [ PREPPERS MENU ]            1. PLATES (플레이트)            2. PASTAS (파스타)            3. ROSE PASTAS (로제 파스타)            4. BOWLS (보울)            5. DOUBLE BOWLS (더블 보울)            6. DESSERTS (디저트)            7. DRINKS (음료)                        [ ORDER MENU ]            8. 주문하기                        [ EXIT MENU ]                        0. 종료        """        private const val MOVE_BACK = "\n0. 뒤로가기"        private const val ORDER_MENU = """            [ ORDER CHECK ]             주문 하시겠습니까?            1. 예    2. 아니오        """    }}