package preppers.weeks2.kiosk.order.controllerimport preppers.weeks2.kiosk.order.Orderimport preppers.weeks2.kiosk.order.repository.OrderRepositoryimport preppers.weeks2.kiosk.product.Productimport java.lang.RuntimeExceptionclass OrderController {    private val orderRepository = OrderRepository()    //Post    fun order(order: Order?) {        orderRepository.save(order)    }    //Get    fun getOrders(): List<Product> {        return orderRepository.selectAll() ?: throw RuntimeException("저장된 주문이 없습니다.")    }}