package preppers.weeks2.kiosk.order.repositoryimport preppers.weeks2.kiosk.order.Orderimport preppers.weeks2.kiosk.product.Productclass OrderRepository {    private val storage = mutableMapOf<Int, Product>()    fun save(order: Order?) {        storage[order!!.idx] = order!!.product    }    fun selectAll(): List<Product> {        return storage.map { it.value }    }}